// App.js

import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route} from 'react-router-dom';
import { Switch } from 'react-router-dom';
import axios from 'axios';

function App() {
  const [dados, setDados] = useState([]);

  useEffect(() => {
    async function fetchData() {
      try {
        const response = await axios.get('http://localhost:5000/dados');
        setDados(response.data);
      } catch (error) {
        console.error('Erro ao buscar dados:', error);
      }
    }
    fetchData();
  }, []);

  return (
    <Router>
      <div>
        <Switch>
          <Route path="/" exact>
            <div>
              <h1>Dados do MySQL</h1>
              <table>
                <thead>
                  <tr>
                    <th>Mês/Ano</th>
                    <th>Soma Valor</th>
                    <th>Variação Percentual</th>
                  </tr>
                </thead>
                <tbody>
                  {dados.map((item, index) => (
                    <tr key={index}>
                      <td>{item.Mes_Ano}</td>
                      <td>{item.Soma_Valor}</td>
                      <td>{item.Variacao_Percentual}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </Route>
          <Route path="/sobre">
            <div>
              <h2>Sobre</h2>
              <p>Esta é uma página de exemplo sobre a nossa aplicação.</p>
            </div>
          </Route>
        </Switch>
      </div>
    </Router>
  );
}

export default App;
