// frontend/src/App.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { BrowserRouter as Router, Link, Route, Switch } from 'react-router-dom';
import OutraPagina from './OutraPagina'; // Importando o componente para a outra página

function App() {
  const [dados, setDados] = useState([]);

  useEffect(() => {
    async function fetchData() {
      try {
        const response = await axios.get('http://localhost:5000/dados');
        setDados(response.data);
      } catch (error) {
        console.error('Erro ao buscar dados:', error);
      }
    }
    fetchData();
  }, []);

  return (
    <Router>
      <div>
        <h1>Dados do MySQL</h1>
        <table>
          <thead>
            <tr>
              <th>Mês/Ano</th>
              <th>Soma Valor</th>
              <th>Variação Percentual</th>
            </tr>
          </thead>
          <tbody>
            {dados.map((item, index) => (
              <tr key={index}>
                <td>{item.Mes_Ano}</td>
                <td>{item.Soma_Valor}</td>
                <td>{item.Variacao_Percentual}</td>
              </tr>
            ))}
          </tbody>
        </table>
        {/* Adicionando um link para a outra página */}
        <Link to="/outra-pagina">
          <button>Navegar para Outra Página</button>
        </Link>
      </div>
      {/* Definindo a rota para a outra página */}
      <Switch>
        <Route path="/outra-pagina" component={OutraPagina} />
      </Switch>
    </Router>
  );
}

export default App;
