import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Link } from 'react-router-dom';
import axios from 'axios';

function Dados() {
  const [dados, setDados] = useState([]);

  useEffect(() => {
    async function fetchData() {
      try {
        const response = await axios.get('http://localhost:5000/dados');
        setDados(response.data);
      } catch (error) {
        console.error('Erro ao buscar dados:', error);
      }
    }
    fetchData();
  }, []);

  return (
    <div>
      <h1>Dados do MySQL</h1>
      <table>
        <thead>
          <tr>
            <th>Mês/Ano</th>
            <th>Soma Valor</th>
            <th>Variação Percentual</th>
          </tr>
        </thead>
        <tbody>
          {dados.map((item, index) => (
            <tr key={index}>
              <td>{item.Mes_Ano}</td>
              <td>{item.Soma_Valor}</td>
              <td>{item.Variacao_Percentual}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

function Sobre() {
  return (
    <div>
      <h2>Sobre</h2>
      <p>Esta é uma página de exemplo sobre a nossa aplicação.</p>
    </div>
  );
}

function App() {
  return (
    <Router>
      <div>
        <nav>
          <ul>
            <li>
              <Link to="/">Home</Link>
            </li>
            <li>
              <Link to="/sobre">Sobre</Link>
            </li>
          </ul>
        </nav>

        <Route path="/" exact component={Dados} />
        <Route path="/sobre" component={Sobre} />
      </div>
    </Router>
  );
}

export default App;
