import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Chart from 'chart.js/auto';
import { Link } from 'react-router-dom';

function Grafico() {
  const [dados, setDados] = useState([]);

  useEffect(() => {
    async function fetchData() {
      try {
        const response = await axios.get('http://localhost:5000/dados');
        setDados(response.data);

        const labels = response.data.map(item => item.Mes_Ano);
        const percentuais = response.data.map(item => item.Variacao_Percentual);

        const ctx = document.getElementById('myChart');
        new Chart(ctx, {
          type: 'line',
          data: {
            labels: labels,
            datasets: [{
              label: 'Variação Percentual',
              data: percentuais,
              fill: false,
              borderColor: 'rgb(255, 99, 132)', // Cor da linha
              backgroundColor: 'rgba(255, 99, 132, 0.2)', // Cor do preenchimento da área abaixo da linha
              tension: 0.4, // Suaviza as curvas do gráfico
              borderWidth: 2, // Largura da linha
              pointRadius: 4, // Tamanho dos pontos
              pointBackgroundColor: 'rgb(255, 99, 132)', // Cor dos pontos
              pointBorderColor: 'rgb(255, 255, 255)', // Cor da borda dos pontos
            }]
          },
          options: {
            responsive: true,
            plugins: {
              title: {
                display: true,
                text: 'Variação Percentual ao Longo do Tempo',
                font: {
                  size: 18
                }
              },
              legend: {
                display: true,
                labels: {
                  color: 'black', // Cor da legenda
                  boxWidth: 15, // Largura da caixa da legenda
                  font: {
                    size: 14
                  }
                }
              }
            },
            scales: {
              x: {
                title: {
                  display: true,
                  text: 'Mês/Ano',
                  font: {
                    size: 16
                  }
                }
              },
              y: {
                title: {
                  display: true,
                  text: 'Variação Percentual',
                  font: {
                    size: 16
                  }
                }
              }
            }
          }
        });
      } catch (error) {
        console.error('Erro ao buscar dados:', error);
      }
    }
    fetchData();
  }, []);

  return (
    <div>
      <h1>Gráfico</h1>
      <Link to="/">Voltar para Home</Link>
      <canvas id="myChart"></canvas>
    </div>
  );
}

export default Grafico;
